dispbutton();window.addEventListener("resize",function(){dispbutton()});function dispbutton(){var c=document.getElementById("button");var a=window.matchMedia("(max-width: 500px)");if(a.matches){document.getElementById("cartoon").style.height="auto";c.innerHTML="Fit to Page";c.style.display="none"}else{c.style.display="inline-block"}}function respond(){var a=document.getElementById("button");if(a.innerHTML=="Fit to Page"){document.getElementById("cartoon").style.height="100vh";a.innerHTML="Expand"}else{if(a.innerHTML=="Expand"){document.getElementById("cartoon").style.height="auto";a.innerHTML="Fit to Page"}}};